function dynaload(){$.ajaxSetup({cache:!0}),$("#demo01").click(function(){$(".modal-content").html(spinner).load(newhtml)})}var newhtml="flat.html",spinner='<div class="loader">Loading...</div>';$(function(){dynaload(),$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var a=$(this.hash);if(a=a.length?a:$("[name="+this.hash.slice(1)+"]"),a.length)return $("html, body").animate({scrollTop:a.offset().top},1e3),!1}})});